package programmers.level1;import java.util.Objects;public class _카드_뭉치 {    public static void main(String[] args) {        String[] c1 = {"i", "drink", "water"},                c2 = {"i", "water", "drink"};        String[] d1 = {"want", "to"},                d2 = {"want", "to"};        String[] g1 = {"i", "want", "to", "drink", "water"},                g2 = {"i", "want", "to", "drink", "water"};        System.err.println(solution(c1, d1, g1));        System.err.println(solution(c2, d2, g2));    }    public static String solution(String[] cards1, String[] cards2, String[] goal) {        int c = 0, d = 0;        boolean cCheck = false, dCheck = false;        for (int i = 0; i < goal.length; i++) {            if(!cCheck && Objects.equals(cards1[c], goal[i])) {                c++;                if(c == cards1.length) cCheck = true;            }else if(!dCheck && Objects.equals(cards2[d], goal[i])) {                d++;                if(d == cards2.length) dCheck = true;            }else {                return "No";            }        }        return "Yes";    }}